function setLanguage(lang) {
    if (!translations[lang]) return;
    localStorage.setItem('passforge_lang', lang);

    document.querySelectorAll('[data-i18n]').forEach(el => {
        const key = el.getAttribute('data-i18n');
        if (translations[lang][key]) {
            if (el.tagName === 'INPUT' || el.tagName === 'TEXTAREA') {
                el.placeholder = translations[lang][key];
            } else {
                el.innerText = translations[lang][key];
            }
        }
    });

    document.querySelectorAll('[data-i18n-html]').forEach(el => {
        const key = el.getAttribute('data-i18n-html');
        if (translations[lang][key]) {
            el.innerHTML = translations[lang][key];
        }
    });

    // Update active state of language buttons
    document.querySelectorAll('.lang-btn').forEach(btn => {
        const btnLang = btn.getAttribute('onclick').includes("'en'") ? 'en' : 'tr';
        btn.classList.toggle('active', btnLang === lang);
    });

    document.querySelectorAll('[data-i18n-alt]').forEach(el => {
        const key = el.getAttribute('data-i18n-alt');
        if (translations[lang][key]) {
            el.alt = translations[lang][key];
        }
    });

    document.querySelectorAll('[data-i18n-src]').forEach(el => {
        const key = el.getAttribute('data-i18n-src');
        if (translations[lang][key]) {
            el.src = translations[lang][key];
        }
    });

    // Update dynamic page title
    const pageKey = 'title_' + window.location.pathname.split('/').pop().replace('.html', '') || 'index';
    if (translations[lang][pageKey]) {
        document.title = translations[lang][pageKey];
    } else if (window.location.pathname === '/' || window.location.pathname === '') {
        document.title = translations[lang]['title_index'];
    }

    document.documentElement.lang = lang;
}

function initDynamicWallpaper() {
    const wallpapers = [
        'wallpaper/wp10008164-cyber-city-4k-desktop-wallpapers.jpg',
        'wallpaper/wp10008251-cyber-city-4k-desktop-wallpapers.jpg',
        'wallpaper/wp11329429-fine-arts-4k-hd-wallpapers.jpg',
        'wallpaper/wp7370112-cyber-city-wallpapers.jpg',
        'wallpaper/wp8612345-cyber-city-anime-wallpapers.jpg'
    ];
    const randomWp = wallpapers[Math.floor(Math.random() * wallpapers.length)];

    let bg = document.querySelector('.body-bg');
    if (!bg) {
        bg = document.createElement('div');
        bg.className = 'body-bg';
        document.body.prepend(bg);
    }

    let overlay = document.querySelector('.body-bg-overlay');
    if (!overlay) {
        overlay = document.createElement('div');
        overlay.className = 'body-bg-overlay';
        document.body.prepend(overlay);
    }

    bg.style.backgroundImage = `url('${randomWp}')`;
}

document.addEventListener('DOMContentLoaded', () => {
    const savedLang = localStorage.getItem('passforge_lang');
    const defaultLang = savedLang || (navigator.language.startsWith('tr') ? 'tr' : 'en');
    setLanguage(defaultLang);
    initDynamicWallpaper();
});





